			'Header Code', // title
			array( $this, 'header_code_1_callback' ), // callback
			'header-grab-options-admin', // page
			'header_grab_options_setting_section' // section
		);

	}

	public function header_grab_options_sanitize($input) {
		$sanitary_values = array();
		if ( isset( $input['url_to_grab_0'] ) ) {
			$sanitary_values['url_to_grab_0'] = sanitize_text_field( $input['url_to_grab_0'] );
		}

		if ( isset( $input['header_code_1'] ) ) {
			$sanitary_values['header_code_1'] = $input['header_code_1'];
		}

		return $sanitary_values;
	}

