		printf(
			'<input class="regular-text" type="text" name="header_grab_options_option_name[url_to_grab_0]" id="url_to_grab_0" value="%s">',
			isset( $this->header_grab_options_options['url_to_grab_0'] ) ? esc_attr( $this->header_grab_options_options['url_to_grab_0']) : ''
		);
